document.addEventListener("DOMContentLoaded", () => {
    function formatarData(data) {
        const opcoes = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(data).toLocaleDateString('pt-BR', opcoes);
    }

    window.adicionarParticipante = function () {
        const container = document.getElementById("presentes-container");
        const input = document.createElement("input");
        input.type = "text";
        input.className = "presentes";
        input.placeholder = "Nome do participante";
        container.appendChild(input);
    };

    window.gerarPrevia = function () {
        const logoBase64 = "data:image/png;base64,";

        const disciplinas = document.getElementById("disciplinas").value;
        const data = formatarData(document.getElementById("data").value);
        const pauta = document.getElementById("pauta").value;
        const acoes = document.getElementById("acoes").value;
        const encerramento = document.getElementById("encerramento").value;
        const participantesArray = Array.from(document.getElementsByClassName("presentes"))
            .map(input => input.value)
            .filter(nome => nome.trim() !== "");
        const participantes = participantesArray.join("<br>");

        let assinaturas = "";
        participantesArray.forEach(nome => {
            assinaturas += `<p style="text-align: center; font-size: 15px;">______________________________________<br>${nome}</p>`;
        });

        const previa = `
            <div style="text-align: center;">
                <img src="${logoBase64}" alt="Logo Uni" style="width: 200px; margin-bottom: 10px;">
            </div>
            <h2 style="text-align: center; font-size: 18px;">ATA DE REUNIÃO DE ALINHAMENTO DAS AÇÕES DAS DISCIPLINAS ${disciplinas}</h2>
            <p style="font-size: 15,5px;">${data}, iniciou-se a reunião sobre o alinhamento das ações das disciplinas de ${disciplinas}.</p>
            <h2 style="font-weight: bold; font-size: 16px;">Presentes:</h2>
            <p style="font-size: 15px;">${participantes}</p>
            <h2 style="font-weight: bold; font-size: 16px;">Pauta da Reunião:</h2>
            <p style="font-size: 15,5px;">${pauta}</p>
            <h2 style="font-weight: bold; font-size: 16px;">Encaminhamentos e Próximas Ações:</h2>
            <p style="font-size: 15,5px;">${acoes}</p>
            <h2 style="font-weight: bold; font-size: 16px;">Encerramento:</h2>
            <p style="font-size: 15,5px;">${encerramento}</p>
            <h2 style="text-align: center; font-size: 16px;">Quixadá-CE, ${data}</h2>
            ${assinaturas}
        `;

        document.getElementById("previa").innerHTML = previa;
    };

    window.gerarWord = function () {
        const previa = document.getElementById("previa").innerHTML;
        const blob = new Blob([`\ufeff<html><body>${previa}</body></html>`], { type: "application/msword;charset=utf-8" });
        const link = document.createElement("a");
        link.href = URL.createObjectURL(blob);
        link.download = "Ata_de_Reuniao.doc";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };
});
